var tab_color_normal = '#D3DDC8';var tab_color_checked = '#f8f1cd';function fun_orderby(field,theForm){if(theForm.order.value=='ASC'){theForm.order.value='DESC';}else{theForm.order.value='ASC';}theForm.orderby.value=field;theForm.page.value='0';theForm.submit();}function toggle_box(theForm){for(z=0; z<theForm.length;z++){if(theForm[z].type =='checkbox' && theForm[z].disabled==false){if(theForm.allcheck.checked == true){theForm[z].checked=true;if(!isNaN(theForm[z].value)){if(document.getElementById('tr'+theForm[z].value)!=null){document.getElementById('tr'+theForm[z].value).bgColor=tab_color_checked;}}}else{theForm[z].checked=false;if(!isNaN(theForm[z].value)){if(document.getElementById('tr'+theForm[z].value)!=null){document.getElementById('tr'+theForm[z].value).bgColor=tab_color_normal;}}}}}}function check_del(theForm, term){n = $(".del:checked").length;if(!n)alert("Please select a record to delete.");else if(confirm("You are going to delete "+n+" "+term+". Do you really want to continue?")){theForm.delreq.value='1';theForm.submit();}}